mapper.select:
select

from tb_review


		AND rd_user = #{keyword}


		AND re_user = #{keyword}


ORDER BY add_time DESC
LIMIT #{start},#{limit}

  	select count(1)
  	from tb_review


  			AND rd_user = #{keyword}


  			AND re_user = #{keyword}


				and id = #{keyword}


  	ORDER BY add_time DESC
